import React from "react";
import Layout from "../src/components/Layout";

const AboutPage = () => {
  const bodyContent = `Specula is an intuitive application for quickly comparing the different phones available on the market.
  Quickly sort by phone names, brand or even display at an instant!`;

  return (
    <Layout>
      <div className="body">
        <h1>About Us</h1>
        <p className="content">{bodyContent}</p>
      </div>

      <style jsx>
        {`
          .body {
            text-align: center;
          }
          .content {
            text-align: left;
          }
        `}
      </style>
    </Layout>
  );
};

export default AboutPage;
