import React, { useEffect, useReducer } from "react";
import PhonesContext from "../src/context/phones-context";
// Components
import Layout from "../src/components/Layout";
import PhoneDashboardPage from "../src/components/PhoneDashboardPage";
// Actions
import { addPhone } from "../src/actions/phones";
// Reducers
import filtersReducer from "../src/reducers/filters";
import phonesReducer from "../src/reducers/phones";
// Phone Selector
import getVisiblePhones from "../src/selectors/phones";

const Index = () => {
  const [phones, phonesDispatch] = useReducer(phonesReducer, []);
  const [filters, filtersDispatch] = useReducer(filtersReducer, {
    text: "",
    sortBy: "release"
  });

  // Phones Initial Render
  useEffect(() => {
    console.log("Successfully Mounted");
    phonesDispatch(
      addPhone({ name: "iPhone 8", release: "Sept 2018", price: 900 })
    );
    phonesDispatch(
      addPhone({ name: "iPhone 11", release: "Oct 2019", price: 1800 })
    );
    phonesDispatch(
      addPhone({ name: "iPhone 13", release: "Jan 2020", price: 3200 })
    );
    phonesDispatch(
      addPhone({ name: "iPhone 13", release: "Jan 2020", price: 4000 })
    );
  }, []);

  useEffect(() => {
    console.log("Successfully Updated");
    getVisiblePhones(phones, filters);
  }, [filters]);

  return (
    <PhonesContext.Provider
      value={{ phones, phonesDispatch, filters, filtersDispatch }}
    >
      <Layout>
        <PhoneDashboardPage />
      </Layout>
    </PhonesContext.Provider>
  );
};

