import React, { useEffect, useReducer } from "react";
import PhonesContext from "../src/context/phones-context";

import filtersReducer from "../src/reducers/filters";
import phonesReducer from "../src/reducers/phones";

import { addPhone } from "../src/actions/phones";

let phonesInitialState = [
  {
    name: "iPhone 11",
    release: "Sept 2019",
    price: 69000,
    brand: "Apple",
    display: "Liquid Retina HD",
    screen: "6.1 in",
    dimensions: "2.98 x 5.94 x 0.33 in"
  },
  {
    name: "iPhone 11 Pro",
    release: "Sept 2019",
    price: 99900,
    brand: "Apple",
    display: "Super Retuna XDR",
    screen: "5.8 in",
    dimensions: "2.81 x 5.67 x 0.32 in"
  },
  {
    name: "iPhone 11 Pro Max",
    release: "Sept 2019",
    price: 109900,
    brand: "Apple",
    display: "Super Retina XDR",
    screen: "6.5 in",
    dimensions: "3.06 x 6.22 x 0.32 in"
  },
  {
    name: "iPhone Xr",
    release: "Sept 2018",
    price: 74900,
    brand: "Apple",
    display: "Liquid Retina HD",
    screen: "6.1 in",
    dimensions: "2.98 x 5.94 x 0.33 in"
  },
  {
    name: "Galaxy Note 10",
    release: "July 2019",
    price: 94999,
    brand: "Samsung",
    display: "FHD+ Dynamic AMOLED",
    screen: "6.3 in",
    dimensions: "2.83 x 5.94 x 0.31 in"
  },
  {
    name: "Galaxy Note 10+",
    release: "July 2019",
    price: 109999,
    brand: "Samsung",
    display: "Quad HD+ Dynamic AMOLED",
    screen: "6.8 in",
    dimensions: "3.04 x 6.39 x 0.31 in"
  },
  {
    name: "Galaxy S10",
    release: "March 2019",
    price: 89999,
    brand: "Samsung",
    display: "Quad HD+ Dynamic AMOLED",
    screen: "6.1 in",
    dimensions: "2.77 x 5.90 x 0.31 in"
  },
  {
    name: "iPhone 11",
    release: "Sept 2019",
    price: 69000,
    brand: "Apple",
    display: "Liquid Retina HD",
    screen: "6.1 in",
    dimensions: "2.98 x 5.94 x 0.33 in"
  },
  {
    name: "iPhone 11",
    release: "Sept 2019",
    price: 69000,
    brand: "Apple",
    display: "Liquid Retina HD",
    screen: "6.1 in",
    dimensions: "2.98 x 5.94 x 0.33 in"
  }
];

const App = ({ Component, pageProps }) => {
  const [phones, phonesDispatch] = useReducer(phonesReducer, []);
  const [filters, filtersDispatch] = useReducer(filtersReducer, {
    text: "",
    sortBy: "release"
  });

  // componentDidMount() - Initial Render Phones
  useEffect(() => {
    console.log("Successfully Mounted");
    phonesDispatch(
      addPhone({ name: "iPhone 8", release: "Sept 2018", price: 900 })
    );
    phonesDispatch(
      addPhone({ name: "iPhone 11", release: "Sept. 2019", price: 1800 })
    );
    phonesDispatch(
      addPhone({ name: "iPhone 12", release: "Jan 2020", price: 3200 })
    );
    phonesDispatch(
      addPhone({
        name: "iPhone 13",
        release: "Jan 2020",
        price: 4000,
        brand: "Apple"
      })
    );
    phonesDispatch(
      addPhone({
        name: "Galaxy Note 8",
        release: "Sept 2018",
        price: 3300,
        brand: "Samsung"
      })
    );
  }, []);

  // Load data from localStorage
  useEffect(() => {
    const phones = JSON.parse(localStorage.getItem("phones"));
    if (phones) {
      phonesDispatch({ type: "POPULATE_PHONES", phones });
    }
  }, []);

  // Save item 'phones' to localStorage
  useEffect(() => {
    localStorage.setItem("phones", JSON.stringify(phones));
  }, [phones]);

  return (
    <PhonesContext.Provider
      value={{ phones, phonesDispatch, filters, filtersDispatch }}
    >
      <Component {...pageProps} />
    </PhonesContext.Provider>
  );
};

export default App;
