import React from "react";
import Layout from "../src/components/Layout";

const AboutPage = () => {
  const bodyContent = `Specula is an intuitive application for quickly comparing the different phones available on the market.
  You can now quickly sort by phone names, brand or even display at an instant!`;

  return (
    <Layout>
      <div className="body">
        <h1 className="title">What is specula?</h1>
        <p className="content">{bodyContent}</p>
      </div>

      <style jsx>
        {`
          .body {
            text-align: center;
            padding: 0 200px;
          }
          .title {
            margin-bottom: 30px;
          }
          .content {
            text-align: left;
          }
        `}
      </style>
    </Layout>
  );
};

export default AboutPage;
