import Layout from "../components/Layout";
import phones from "../database/index";
import { v4 as uuidv4 } from "uuid";

const Index = () => {
  return (
    <Layout>
      <div className="body">
        <ul className="container">{renderPhones()}</ul>
      </div>
      <style jsx>
        {`
          .container {
            display: flex;
            justify-content: center;
          }
        `}
      </style>
    </Layout>
  );
};

const renderPhones = () => {
  const phoneList = phones.map(phone => {
    return (
      <li key={uuidv4()} className="card">
        <div className="title">
          <a>{phone.name}</a>
        </div>
        <ul>
          <li>{phoneDescription(phone)}</li>
        </ul>
        <style jsx>
          {`
            li {
              list-style: none;
            }
            .card {
              border: 1px solid black;
            }
            .title {
            }
          `}
        </style>
      </li>
    );
  });
  return phoneList;
};

const phoneDescription = phone => {
  const keys = Object.keys(phone).map(key => {
    return <p key={uuidv4()}>{`${key}: ${phone[key]}`}</p>;
  });
  return keys;
};

export default Index;
