import React, { useContext } from "react";
import PhoneListItem from "./PhoneListItem";
import getVisiblePhones from "../selectors/phones";
import PhonesContext from "../context/phones-context";

const PhoneList = () => {
  const { phones } = useContext(PhonesContext);
  const { filters } = useContext(PhonesContext);

  useEffect(() => {
    console.log("Successfully Updated");
    getVisiblePhones(phones, filters);
  }, [filters]);

  return (
    <ul className="phone-list">
      {phones.map(phone => (
        <PhoneListItem key={phone.id} {...phone} />
      ))}
      <style jsx>
        {`
          .phone-list {
            display: flex;
            justify-content: space-evenly;
            flex-wrap: wrap;
            padding-top: 100px;
          }
        `}
      </style>
    </ul>
  );
};

export default PhoneList;
