import React, { useContext } from "react";
import {
  setTextFilter,
  sortByPrice,
  sortByRelease,
  sortByBrand
} from "../actions/filters";
import PhonesContext from "../context/phones-context";

const PhoneListFilters = () => {
  const { filters, filtersDispatch } = useContext(PhonesContext);

  useEffect(() => {
    console.log("Successfully Updated");
    getVisiblePhones(phones, filters);
  }, [filters]);

  return (
    <div>
      <input
        type="text"
        value={filters.text}
        onChange={e => {
          filtersDispatch(setTextFilter(e.target.value));
        }}
        placeholder="search phone"
      />
      <select
        value={filters.sortBy}
        onChange={e => {
          let sortBy;
          switch (e.target.value) {
            case "release":
              sortBy = sortByRelease();
              break;
            case "price":
              sortBy = sortByPrice();
              break;
            case "brand":
              sortBy = sortByBrand();
              break;
          }
          filtersDispatch(sortBy);
        }}
      >
        <option value="release">Release</option>
        <option value="price">Price</option>
        <option value="brand">Brand</option>
      </select>
    </div>
  );
};

export default PhoneListFilters;
